model:model:
$identify("org/mathdox/formulaeditor/modules//.js");

$require("org/mathdox/parsing/ParserGenerator.js");
$require("org/mathdox/formulaeditor/parsing/openmath/OpenMathParser.js");
$require("org/mathdox/formulaeditor/parsing/expression/ExpressionParser.js");
$require("org/mathdox/formulaeditor/semantics/MultaryOperation.js");

$main(function(){

  /**
   * Define a semantic tree node that represents ..
   */
  org.mathdox.formulaeditor.semantics. =
    $extend(org.mathdox.formulaeditor.semantics.MultaryOperation, {

      symbol : {

        onscreen : "",
        openmath : "<OMS cd='' name=''/>",
        mathml   : "<mo></mo>"

      },

      precedence : 

    });

  /**
   * Extend the OpenMathParser object with parsing code for ..
   */
  org.mathdox.formulaeditor.parsing.openmath.OpenMathParser =
    $extend(org.mathdox.formulaeditor.parsing.openmath.OpenMathParser, {

    /**
     * Returns an equality object based on the OpenMath node.
     */
    handle : function(node) {

      // parse the children of the OMA
      var children = node.getChildNodes();
      var operands = [];
      for (var i=1; i<children.length; i++) {
        operands.push(this.handle(children.item(i)));
      }

      // construct the corresponding object
      var result = new org.mathdox.formulaeditor.semantics.();
      result.initialize.apply(result, operands);
      return result;


    }

  });

  /**
   * Extend the ExpressionParser object with parsing code for the equality sign.
   */
  var semantics = org.mathdox.formulaeditor.semantics;
  var pG = new org.mathdox.parsing.ParserGenerator();

  if ( "" == "" ) {
    // only one expression, same on screen
    org.mathdox.formulaeditor.parsing.expression.ExpressionParser =
      $extend(org.mathdox.formulaeditor.parsing.expression.ExpressionParser, {

    // expression =  | super.expression
      expression : function() {
        var parent = arguments.callee.parent;
        pG.alternation(
          pG.rule(""),
          parent.expression).apply(this, arguments);
      },

      //  = 
      //    expression "" expression
       :
        pG.transform(
          pG.concatenation(
            pG.rule("expression"),
            pG.literal(""),
            pG.rule("expression"),
            pG.repetitionplus(
              pG.concatenation(
                pG.literal(""),
                pG.rule("expression")
              )
            )
          ),
          function(result) {
            var retval = new semantics.();
            var operands = [];
            var i;

            for (i=0; 2*i<result.length; i++) {
              operands[i] = result[2*i];
            }
            retval.operands = operands;

            return retval;
          }
        )

    });
  } else { // allow alternative as displayed on the screen
    org.mathdox.formulaeditor.parsing.expression.ExpressionParser =
      $extend(org.mathdox.formulaeditor.parsing.expression.ExpressionParser, {

      // expression =  | 
      //   alt | super.expression
      expression : function() {
        var parent = arguments.callee.parent;
        pG.alternation(
          pG.rule(""),
          pG.rule("alt"),
          parent.expression).apply(this, arguments);
      },

      //  = 
      //    expression "" expression
       :
        pG.transform(
          pG.concatenation(
            pG.rule("expression"),
            pG.literal(""),
            pG.rule("expression"),
            pG.repetitionplus(
              pG.concatenation(
                pG.literal(""),
                pG.rule("expression")
              )
            )
          ),
          function(result) {
            var retval = new semantics.();
            var operands = [];
            var i;

            for (i=0; 2*i<result.length; i++) {
              operands[i] = result[2*i];
            }
            retval.operands = operands;

            return retval;
          }
        ),

      // alt = 
      //    expression "" expression
      alt :
        pG.transform(
          pG.concatenation(
            pG.rule("expression"),
            pG.literal(""),
            pG.rule("expression"),
            pG.repetitionplus(
              pG.concatenation(
                pG.literal(""),
                pG.rule("expression")
              )
            )
          ),
          function(result) {
            var retval = new semantics.();
            var operands = [];
            var i;

            for (i=0; 2*i<result.length; i++) {
              operands[i] = result[2*i];
            }
            retval.operands = operands;

            return retval;
          }
        )

    });
  }
});
