model:model:
$require("org/mathdox/formulaeditor/semantics/Keyword.js");

$main(function(){

  with (org.mathdox.formulaeditor.semantics) {
    /**
     * Define a semantic tree node that represents the 
     * . keyword.
     */
    var cd = "";
    var name = "";
    var symbol = { 
      onscreen: "", 
      openmath : null, // use default with model:cd and model:name
      mathml : ""
    };

    org.mathdox.formulaeditor.parsing.expression.KeywordList[""] = new Keyword(cd, name, symbol);

    if ( "" != "" ) {
      org.mathdox.formulaeditor.parsing.expression.KeywordList[""] = new Keyword(cd, name, symbol);
    }

    org.mathdox.formulaeditor.parsing.openmath.KeywordList["__"] = new Keyword(cd, name, symbol);
  }
});
