model:model:
$require("org/mathdox/formulaeditor/semantics/Keyword.js");

$main(function(){

  /**
   * Define a semantic tree node that represents the 
   * . keyword.
   */
  org.mathdox.formulaeditor.semantics. =
    $extend(org.mathdox.formulaeditor.semantics.Keyword, {
      cd : "",
      name : "",
      symbol : {

        onscreen : "",
	openmath : null, // use default with model:cd and model:name
	mathml : ""

      }
    })

  org.mathdox.formulaeditor.parsing.expression.KeywordList[""] =
    new org.mathdox.formulaeditor.semantics.("", "")

  if ( "" != "" ) {
    org.mathdox.formulaeditor.parsing.expression.KeywordList[""] = 
      new org.mathdox.formulaeditor.semantics.("", "") 
  }


  org.mathdox.formulaeditor.parsing.openmath.KeywordList["__"] =
    new org.mathdox.formulaeditor.semantics.("", "")

})
