model:model:
$main(function(){

  /**
   * Define a semantic tree node that represents the 
   * function.
   */
  org.mathdox.formulaeditor.semantics. =
    $extend(org.mathdox.formulaeditor.semantics.MultaryOperation, {

      symbol : {

        onscreen : ["(", ")"],
	openmath : "<OMS cd='' name=''/>"

      },

      precedence : 0

    })

  /**
   * Extend the OpenMathParser object with parsing code for
   * ..
   */
  org.mathdox.formulaeditor.parsing.openmath.OpenMathParser =
    $extend(org.mathdox.formulaeditor.parsing.openmath.OpenMathParser, {

      /**
       * Returns a  object based on the OpenMath node.
       */
      handle : function(node) {

        // parse the children of the OMA
        var children = node.getChildNodes();
        var operands = new Array(children.getLength()-1);
        for (var i=1; i<children.length; i++) {
          operands[i-1] = this.handle(children.item(i))
        }

        // construct a  object
        var result = new org.mathdox.formulaeditor.semantics.();
        result.initialize.apply(result, operands);
        return result;

      }

    })

  /**
   * Extend the ExpressionParser object with parsing code for  operations.
   */
  with( org.mathdox.formulaeditor.semantics          ) {
  with( org.mathdox.formulaeditor.parsing.expression ) {
  with( new org.mathdox.parsing.ParserGenerator()    ) {

    org.mathdox.formulaeditor.parsing.expression.ExpressionParser =
      $extend(org.mathdox.formulaeditor.parsing.expression.ExpressionParser, {

        // expression6 =  | super.expression6
        expression6 : function() {
          var parent = arguments.callee.parent;
          alternation(
            rule(""),
            parent.expression6
          ).apply(this, arguments);
        },

        //  = "(" expression ("," expression)* ")"
         :
          transform(
            concatenation(
              literal("("),
              rule("expression"),
              repetition(
                concatenation(
                  literal(","),
                  rule("expression")
                )
              ),
              literal(")")
            ),
            function(result) {
              var array = new Array();
              for (var i=1; i+1<result.length; i=i+2) {
                array.push(result[i]);
              }
              var  = new ();
              .initialize.apply(, array);
              return ;
            }
          )

      })

  }}}


})
